mapper.update:

update `order`
set order_user_id = #{order_user_id,jdbcType=INTEGER},
  order_lent_location_id = #{order_lent_location_id,jdbcType=INTEGER},
  order_pobk_id = #{order_pobk_id,jdbcType=INTEGER},
  order_create_time = #{order_create_time,jdbcType=TIMESTAMP},
  order_has_finished = #{order_has_finished,jdbcType=INTEGER},
  order_finish_time=#{order_finish_time,jdbcType=INTEGER}
  order_revert_location_id = #{order_revert_location_id,jdbcType=INTEGER},
  order_cost = #{order_cost,jdbcType=REAL}
where order_id = #{order_id,jdbcType=INTEGER}

mapper.select:
select

from `order`
where 1=1

  and `order_lent_location_id`=#{order_lent_location_id,jdbcType=INTEGER}


  and `order_revert_location_id`=#{order_revert_location_id,jdbcType=INTEGER}


  and `order_user_id`=#{order_user_id,jdbcType=INTEGER}

order by `order_create_time` desc

select

from `order`
where 1=1

  and `order_user_id`=#{order_user_id,jdbcType=INTEGER}


  and `order_has_finished`=#{order_has_finished,jdbcType=INTEGER}

limit 1
